mapper.select:
SELECT * FROM iw_blogs WHERE is_deleted = 'N' AND is_visible = 't'
AND blog_seq = #{blogSeq}
AND lang = #{lang}
ORDER BY blog_seq DESC

mapper.insert:
INSERT INTO iw_blogs
( blog_seq
, title
, content
, thumbnail_seq
, is_visible
, is_deleted
, created_date
, created_by
, url
, contentType
, lang
)
VALUES
( #{blogSeq}
, #{title}
, #{content}
, #{thumbnailSeq}
, #{isVisible}
, 'N'
, now()
, ''
, #{url}
, #{contentType}
, #{lang}
)

mapper.update:
UPDATE iw_blogs 
SET updated_date = now()
    , updated_by = '',

    , title = #{title}


    , content = #{content}


    , thumbnail_seq = #{thumbnailSeq}


    , is_visible = #{isVisible}

WHERE blog_seq = #{blogSeq}

UPDATE iw_blogs 
    SET is_deleted = 'Y'
WHERE blog_seq = #{blog_seq}
